import {createElement, useState} from 'react'
import './App.css';

function App() {
  const [addContent, setAddContent] = useState('')
  const [category, setCategory] = useState()
  const [todo, setTodo] = useState([])

  const handleAddOnChange = (event) =>{
    setAddContent(event.target.value)
  } 

  const handleCategoryOnChange = (event) =>{
    setCategory(event.target.value)
  }

  const handleAddClick = () =>{
    if(addContent === ''){
      alert('Text content can be empty!')
    }

    const newTodo = { 
      content: addContent,
      category: category,
      createAt: new Date(),
      updateAt: new Date()
    }

    setTodo([...todo, newTodo]);
    setAddContent('');
    setCategory('')

    console.log(todo);
  }

  return (
    <div className="App">
      <h1>What's the plan today?</h1>
      <input className='input-todo' onChange={handleAddOnChange} type="text " value={addContent}></input>
      <br></br>
      <p>Category: </p>
      <input onChange={handleCategoryOnChange} type='number' value={ca}></input>
      <br></br>
      <button className='add-button' onClick={handleAddClick}>Add</button>
      <p>todo list: </p>
      {
        todo.map((e) =>{
          return (
            <div>
              <p>{e.content}</p>
              <p>{e.category}</p>
            </div>
          )
        })
      }
      <div>

      </div>
    </div>
  );
}

export default App;
